<?php

namespace Database\Seeders;

use Illuminate\Database\Seeder;
use App\Models\Article;
use App\Models\Category;
use App\Models\User;
use Illuminate\Support\Facades\Schema;
use Illuminate\Support\Str;

class ArticleSeeder extends Seeder
{
    public function run(): void
    {
        // 1. Xóa sạch dữ liệu cũ để tránh trùng lặp
        Schema::disableForeignKeyConstraints();
        Article::truncate();
        Schema::enableForeignKeyConstraints();

        // 2. Lấy dữ liệu mồi (Tác giả và Danh mục)
        $user = User::first();
        $category = Category::where('code', 'LUC_QUAN')->first() 
                    ?? Category::create(['name' => 'Lục Quân', 'code' => 'LUC_QUAN']);

        if (!$user) {
            $this->command->error('Lỗi: Bạn cần chạy UserSeeder trước!');
            return;
        }

        // 3. Dữ liệu bài viết với link ảnh đã kiểm tra (Wikimedia cực kỳ ổn định)
        $articlesRaw = [
            [
                'title' => 'Sư đoàn 324: Sự hình thành và những bước đi đầu tiên (1955-1966)',
                'body' => 'Tháng 6/1955, Bộ Tổng Tư lệnh thành lập Sư đoàn 324 tại Thanh Hóa. Đây là đơn vị tập hợp các cán bộ chiến sĩ miền Nam tập kết ra Bắc. Năm 1957, Sư đoàn vinh dự được đón Bác Hồ về thăm.',
                'img' => 'data:image/jpeg;base64,',
            ],
            [
                'title' => 'Sư đoàn 324: Đoàn Ngự Bình trên hỏa tuyến Đường 9',
                'body' => 'Năm 1966, Sư đoàn mang bí danh "Nông trường 1-5" hành quân vào chiến trường Trị - Thiên. Tại đây, đơn vị đã trực tiếp đối đầu với quân viễn chinh Mỹ, bắn rơi 46 máy bay địch.',
                'img' => 'data:image/jpeg;base64,'
            ],
            [
                'title' => 'Sư đoàn 324: Nghĩa vụ quốc tế tại Lào (1977 - 1987)',
                'body' => 'Sư đoàn đã giúp nhân dân Lào đập tan sào huyệt phỉ tại Bom Lọng năm 1985, giải phóng 5.000 dân và hỗ trợ xây dựng các công trình thủy lợi Na Mon, Mường Phương.',
                'img' => 'https://file3.qdnd.vn/data/images/0/2022/07/12/quynhtrang/ba%20con%20xa%20kim%20lien%20don%20nguoi%20ve%20tham%201957.jpeg?dpi=150&quality=100&w=870'
            ],
            [
                'title' => 'Sư đoàn 324: Giải phóng Cố đô Huế và Chiến dịch Hồ Chí Minh',
                'body' => 'Ngày 26/03/1975, Sư đoàn 324 giải phóng hoàn toàn Huế. Sau đó, đơn vị thần tốc tiến về Đà Nẵng và tham gia mũi tấn công giải phóng Sài Gòn ngày 30/04/1975.',
                'img' => 'data:image/jpeg;base64,'
            ],
        ];

        // 4. Tự động kiểm tra cột và chèn dữ liệu
        $columns = Schema::getColumnListing('articles');

        foreach ($articlesRaw as $item) {
            $data = [
                'category_id' => $category->id,
                'author_id'   => $user->id,
                'created_at'  => now(),
                'updated_at'  => now(),
            ];

            // Gán dữ liệu dựa trên cột thực tế trong DB
            if (in_array('title', $columns)) $data['title'] = $item['title'];
            if (in_array('slug', $columns)) $data['slug'] = Str::slug($item['title']);
            if (in_array('body', $columns)) $data['body'] = $item['body'];
            elseif (in_array('content', $columns)) $data['content'] = $item['body'];

            // Gán đoạn trích (Excerpt) - Cắt từ body
            if (in_array('excerpt', $columns)) {
                $data['excerpt'] = Str::limit(strip_tags($item['body']), 150);
            }

            // Gán ảnh
            if (in_array('image_url', $columns)) {
             $data['image_url'] = $item['img'];
}

            Article::create($data);
        }

        $this->command->info('Đã xóa sạch data cũ và nạp mới thành công với link ảnh ổn định!');
    }
}